import React, { useState } from 'react';
import Calendar from 'react-calendar';
import * as Hebcal from 'hebcal';
import './App.css';

const monthNames = ['תשרי', 'חשון', 'כסלו', 'טבת',
                    'שבט', 'אדר', 'ניסן', 'אייר',
                    'סיון', 'תמוז', 'אב', 'אלול']

function App() {

  const [date, setDate] = useState(new Date());
  const tileContent = (props) => {
    var day = new Hebcal.HDate(new Date(props.date));
    const formattedDate = `${Hebcal.gematriya(day.day)} ${monthNames[(day.month + 5) % 12]}`
    const holiday = day.holidays();
    let holidayString = '';
    if (holiday.length) {
      holidayString = holiday[0].desc[2];
      holidayString = holidayString.replace(')', '(')
      holidayString = holidayString.replace('(', ')')
    }
    return (
      <div>
        <div className='hebrew-string'>{formattedDate}</div>
        <div className='hebrew-string'>{holidayString}</div>
      </div>
    )
  }

  return (
    <div>
    <Calendar
      calendarType="Hebrew"
      tileContent={tileContent}
      onChange={setDate}
      value={date}
    />
  </div>
);
}

export default App;
