import React, { useState } from 'react';
import Calendar from 'react-calendar';
import * as Hebcal from 'hebcal';
import './App.css';

function App() {

  const [date, setDate] = useState(new Date());
  const tileContent = (props) => {
    var day = new Hebcal.HDate(new Date(props.date));
    const formattedDate = `${Hebcal.gematriya(day.day)}`
    console.log(day)
    return <div>{formattedDate}</div>
  }

  return (
    <div>
    <Calendar
      calendarType="Hebrew"
      tileContent={tileContent}
      onChange={setDate}
      value={date}
    />
  </div>
);
}

export default App;
