import React, { useState } from 'react';
import Calendar from 'react-calendar';
import * as Hebcal from 'hebcal';
import './App.css';

const monthNames = ['', 'ניסן', 'אייר', 'סיון', 'תמוז', 'אב', 'אלול', 
                    'תשרי', 'חשון', 'כסלו', 'טבת', 'שבט', 'אדר', 'אדר ב']

function App() {

  const [date, setDate] = useState(new Date());
  const tileContent = (props) => {
    switch(props.view) {
      case "month":
        var day = new Hebcal.HDate(new Date(props.date));
        const formattedDate = `${Hebcal.gematriya(day.day)} ${monthNames[day.month]}`
        const holiday = day.holidays();
        let holidayString = '';
        if (holiday.length) {
          holidayString = holiday[0].desc[2];
          holidayString = holidayString.replace('(', '');
          holidayString = holidayString.replace(')', '');
        }
        return (
          <div>
            <div className='hebrew-string'>{formattedDate}</div>
            <div className='hebrew-string'>{holidayString}</div>
          </div>
        );
        case "year":
          const startDate = new Date(props.date);
          const endDate = (new Date(props.date)).setMonth(startDate.getMonth + 1);
          console.log('year', date)
          break;
      }
  }

  return (
    <div>
    <Calendar
      calendarType="Hebrew"
      tileContent={tileContent}
      onChange={setDate}
      maxDetail="month"
      minDetail="decade"
      value={date}
    />
  </div>
);
}

export default App;
